script:
const e = ntb.app.workspace.activeLeaf.view?.editor;
if (!e) return;
const c = e.getValue();
const b = c.startsWith('---\n') ? c.replace(/^---\n[\s\S]*?\n---\n*/, '') : c;
await navigator.clipboard.writeText(b);
new Notice(ntb.t('api.msg.clipboard-copied'));

const i = ntb.getActiveItem();
const t = [...Object.values(ntb.app.customCss.themes), { name: '' }];
const n = t[(t.findIndex(theme => theme.name === ntb.app.vault.getConfig('cssTheme')) + 1) % t.length].name;
ntb.app.customCss.setTheme(n);
if (n) new Notice(n);

const p = ntb.app.internalPlugins.getPluginById('workspaces');
const ws = Object.keys(p?.instance.workspaces);
const a = p?.instance.activeWorkspace;
const i = (ws.indexOf(a) + 1) % ws.length;
p?.instance.loadWorkspace(ws[i]);
new Notice(ws[i]);

const fn = 'File.md'; // CHANGE THIS
const f = ntb.app.vault.getAbstractFileByPath(fn);
if (!f) { new Notice(ntb.t('api.msg.file-not-exist')); return; };
const txt = await ntb.prompt({ large: true });
if (txt) { const c = await ntb.app.vault.read(f); await ntb.app.vault.modify(f, c + '\n\n' + txt); }

const p = ntb.app.workspace.getActiveFile()?.parent.path ?? '/';
await ntb.app.workspace.getLeaf(true).setViewState({ type: 'search' });
const l = ntb.app.workspace.getLeavesOfType('search')[0];
const se = l.containerEl.querySelector('.search-input-container > input');
se.value = (p === '/') ? '' : 'path:"'+p+'" ';
se.trigger('input');
se.focus();

const ed = ntb.app.workspace.activeEditor?.editor;
const ln = ed.getValue().split('\n');
let idx = ed.getCursor().line;
const r = /^\s*>/;
while (idx < ln.length && r.test(ln[idx])) idx++;
while (idx < ln.length && !r.test(ln[idx])) idx++;
if (idx >= ln.length) { idx = 0; while (idx < ln.length && !r.test(ln[idx])) idx++; }
if (idx < ln.length) { ed.setCursor({ line: idx, ch: 0 }); }

const e = ntb.app.workspace.activeEditor?.editor;
const c = e.getCursor();
const lines = e.getValue().split('\n');
for (let i = c.line + 1; i !== c.line; i = (i + 1) % lines.length) { if (/^#{1,6}\s/.test(lines[i])) { e.setCursor({ line: i, ch: 0 }); break; } }

const e = ntb.app.workspace.activeEditor?.editor;
const c = e.getCursor();
const lines = e.getValue().split('\n');
for (let i = (c.line - 1 + lines.length) % lines.length; i !== c.line; i = (i - 1 + lines.length) % lines.length) { if (/^#{1,6}\s/.test(lines[i])) { e.setCursor({ line: i, ch: 0 }); break; } }

const src = '.obsidian/plugins/note-toolbar/data.json';
const date = new Date().toISOString().slice(0, 10).replace(/-/g, '');
const dest = `note-toolbar-${date}-data.json`;
ntb.app.vault.adapter.read(src).then(data => { ntb.app.vault.adapter.write(dest, data); new Notice(ntb.t('api.msg.file-created', {filename: dest})); });

const w = 'My Workspace'; // CHANGE THIS
ntb.app.internalPlugins.getPluginById('workspaces')?.instance.loadWorkspace(w);
new Notice(w);

const i = ntb.getActiveItem();
if (ntb.app.vault.getConfig('theme') === 'obsidian') { ntb.app.vault.setConfig('theme', 'obsidian-light'); i?.setIcon('moon'); }
else { ntb.app.vault.setConfig('theme', 'obsidian'); i?.setIcon('sun'); }

let tc = 'myclass'; // CHANGE THIS
const af = ntb.app.workspace.getActiveFile(); if (!af) return;
await ntb.app.fileManager.processFrontMatter(af, (fm) => { fm['cssclasses'] = fm['cssclasses'] || [];
fm['cssclasses'] = fm['cssclasses'].includes(tc) ? fm['cssclasses'].filter((cls) => cls !== tc) : [...fm['cssclasses'], tc]; });

const h = activeDocument.querySelector('.view-header'), s = activeDocument.querySelector('.status-bar');
if (h && s) {
 if (h.style.display === 'none') {
 h.style.display = '';
 s.style.display = '';
 ntb.app.workspace.leftSplit?.expand();
 // ntb.app.workspace.rightSplit?.expand();
 await ntb.app.commands.executeCommandById('note-toolbar:show-properties');
 }
 else {
 h.style.display = 'none';
 s.style.display = 'none';
 ntb.app.workspace.leftSplit?.collapse();
 // ntb.app.workspace.rightSplit?.collapse();
 await ntb.app.commands.executeCommandById('note-toolbar:hide-properties');
 }
 }

const vm = ntb.app.vault.getConfig('vimMode');
ntb.app.vault.setConfig('vimMode', !vm);
ntb.getActiveItem()?.setIcon(vm ? 'keyboard' : 'edit-2');
